import React, { useState } from "react";
import { TextField, Button } from "@mui/material";

interface BidAuctionProps {
  auctionId: number;
  currentBid: number;
  onBidSuccess: () => void;
}

const BidAuction: React.FC<BidAuctionProps> = ({
  auctionId,
  currentBid,
  onBidSuccess,
}) => {
  const [bidAmount, setBidAmount] = useState(Math.floor(currentBid) + 100);
  const [message, setMessage] = useState("");

  const handleBid = async () => {
    const token = localStorage.getItem("accessToken");
    const response = await fetch(
      `http://localhost:8000/api/auctions/${auctionId}/bid/`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ bid_amount: bidAmount }),
      }
    );

    if (response.ok) {
      setMessage("Bid placed successfully!");
      onBidSuccess();
    } else {
      const data = await response.json();
      setMessage(data.error || "Failed to place bid.");
    }
  };

  const handleBidAmountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = Number(e.target.value);
    if (!isNaN(value)) {
      setBidAmount(Math.ceil(value / 100) * 100); // Round up to the nearest £100
    } else {
      setBidAmount(0); // Reset to 0 if input is invalid
    }
  };

  return (
    <div>
      <h2>Current Bid: £{currentBid}</h2>
      <TextField
        type="number"
        value={bidAmount}
        onChange={handleBidAmountChange}
        inputProps={{ min: currentBid + 100 }}
        label="Enter your bid"
        variant="outlined"
        fullWidth
      />
      <Button onClick={handleBid} variant="contained" sx={{ mt: 2 }}>
        Place Bid
      </Button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default BidAuction;
