import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Box, Container, Typography, Button } from "@mui/material";
import BidAuction from "../components/BidAuction";

const AuctionDetail = () => {
  const { id } = useParams<{ id: string }>();
  const [auction, setAuction] = useState<any>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAuction = async () => {
      const response = await fetch(`http://localhost:8000/api/auctions/${id}/`);
      if (response.ok) {
        const data = await response.json();
        setAuction(data);
      }
    };

    fetchAuction();
  }, [id]);

  useEffect(() => {
    if (auction && new Date(auction.end_time) < new Date()) {
      navigate("/payment");
    }
  }, [auction, navigate]);

  if (!auction) return <div>Loading...</div>;

  return (
    <Container>
      <Typography variant="h4">{auction.name}</Typography>
      <Typography variant="h6">Current Bid: Â£{auction.current_bid}</Typography>
      <Typography variant="body1">Time Left: {auction.time_left}</Typography>
      <BidAuction auctionId={auction.id} currentBid={auction.current_bid} />
      <Button variant="contained" onClick={() => window.history.back()}>
        Back
      </Button>
    </Container>
  );
};

export default AuctionDetail;
