import { useState, useEffect } from "react";
import {
  Box,
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
} from "@mui/material";
import { useParams } from "react-router-dom";

interface Auction {
  id: number;
  name: string;
  year: number;
  current_bid: number;
  starting_bid: number;
  buy_now_price: number;
  time_left: string;
}

interface UserProfile {
  username: string;
  date_joined: string;
  bio: string;
  active_auctions: Auction[];
  past_auctions: Auction[];
}

const Profile = () => {
  const { username } = useParams<{ username: string }>();
  const [profile, setProfile] = useState<UserProfile | null>(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(
          `http://localhost:8000/api/users/${username}/`
        );
        if (response.ok) {
          const data = await response.json();
          setProfile(data);
        }
      } catch (error) {
        console.error("Failed to fetch profile:", error);
      }
    };

    fetchProfile();
  }, [username]);

  if (!profile) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ py: 4 }}>
        <Typography variant="h4" gutterBottom>
          {profile.username}'s Profile
        </Typography>
        <Typography variant="body1" gutterBottom>
          Member since: {new Date(profile.date_joined).toLocaleDateString()}
        </Typography>
        <Typography variant="body1" gutterBottom>
          Bio: {profile.bio || "No bio available."}
        </Typography>

        <Typography variant="h5" sx={{ mt: 4 }}>
          Active Auctions
        </Typography>
        <Grid container spacing={4}>
          {profile.active_auctions.length > 0 ? (
            profile.active_auctions.map((auction) => (
              <Grid item key={auction.id} xs={12} sm={6} md={4}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{auction.name}</Typography>
                    <Typography variant="body2">
                      Current Bid: £{auction.current_bid}
                    </Typography>
                    <Typography variant="body2">
                      Time Left: {auction.time_left}
                    </Typography>
                    <Button variant="contained" color="primary" sx={{ mt: 2 }}>
                      View Auction
                    </Button>
                  </CardContent>
                </Card>
              </Grid>
            ))
          ) : (
            <Typography>No active auctions.</Typography>
          )}
        </Grid>

        <Typography variant="h5" sx={{ mt: 4 }}>
          Past Auctions
        </Typography>
        <Grid container spacing={4}>
          {profile.past_auctions.length > 0 ? (
            profile.past_auctions.map((auction) => (
              <Grid item key={auction.id} xs={12} sm={6} md={4}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{auction.name}</Typography>
                    <Typography variant="body2">
                      Final Bid: £{auction.current_bid}
                    </Typography>
                    <Typography variant="body2">
                      Year: {auction.year}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))
          ) : (
            <Typography>No past auctions.</Typography>
          )}
        </Grid>
      </Box>
    </Container>
  );
};

export default Profile;
