import {
  Box,
  Container,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Paper,
  Grid,
  Divider,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import PersonIcon from "@mui/icons-material/Person";
import GavelIcon from "@mui/icons-material/Gavel";
import DeleteIcon from "@mui/icons-material/Delete";
import { useState, useEffect } from "react";

interface User {
  id: number;
  username: string;
  email: string;
  date_joined: string;
  is_staff: boolean;
}

const Admin = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [expanded, setExpanded] = useState<string | false>(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [userToDelete, setUserToDelete] = useState<User | null>(null);

  const handleChange =
    (panel: string) => (event: React.SyntheticEvent, isExpanded: boolean) => {
      setExpanded(isExpanded ? panel : false);
    };

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/auth/users/");
      if (response.ok) {
        const data = await response.json();
        setUsers(data);
      }
    } catch (error) {
      console.error("Failed to fetch users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleDeleteClick = (user: User) => {
    setUserToDelete(user);
    setDeleteDialogOpen(true);
  };

  const handleDeleteConfirm = async () => {
    if (!userToDelete) return;

    try {
      const response = await fetch(
        `http://localhost:8000/api/auth/users/${userToDelete.id}/`,
        {
          method: "DELETE",
        }
      );

      if (response.ok) {
        setUsers(users.filter((user) => user.id !== userToDelete.id));
      } else {
        const data = await response.json();
        alert(data.error || "Failed to delete user");
      }
    } catch (error) {
      alert("Network error");
    }

    setDeleteDialogOpen(false);
    setUserToDelete(null);
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3, backgroundColor: "#f5f5f5" }}>
      <Container maxWidth="lg">
        <Typography variant="h4" component="h1" gutterBottom color="#1a1a1a">
          Admin Dashboard
        </Typography>

        <Grid container spacing={3}>
          {/* Users Section */}
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2, mb: 2 }}>
              <Typography
                variant="h6"
                gutterBottom
                sx={{ display: "flex", alignItems: "center" }}
              >
                <PersonIcon sx={{ mr: 1, color: "#ff0000" }} />
                Users Management
              </Typography>
              <Divider sx={{ mb: 2 }} />
              {users.map((user) => (
                <Accordion
                  key={user.id}
                  expanded={expanded === `user-${user.id}`}
                  onChange={handleChange(`user-${user.id}`)}
                >
                  <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    sx={{
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "space-between",
                    }}
                  >
                    <Typography>{user.username}</Typography>
                    {!user.is_staff && (
                      <IconButton
                        onClick={(e) => {
                          e.stopPropagation();
                          handleDeleteClick(user);
                        }}
                        sx={{ color: "#ff0000" }}
                      >
                        <DeleteIcon />
                      </IconButton>
                    )}
                  </AccordionSummary>
                  <AccordionDetails>
                    <Box>
                      <Typography>
                        <strong>Email:</strong> {user.email}
                      </Typography>
                      <Typography>
                        <strong>Joined:</strong>{" "}
                        {new Date(user.date_joined).toLocaleDateString()}
                      </Typography>
                      <Typography>
                        <strong>Role:</strong>{" "}
                        {user.is_staff ? "Admin" : "User"}
                      </Typography>
                    </Box>
                  </AccordionDetails>
                </Accordion>
              ))}
            </Paper>
          </Grid>

          {/* Auctions Section */}
          <Grid item xs={12} md={6}>
            <Paper sx={{ p: 2 }}>
              <Typography
                variant="h6"
                gutterBottom
                sx={{ display: "flex", alignItems: "center" }}
              >
                <GavelIcon sx={{ mr: 1, color: "#ff0000" }} />
                Auctions Management
              </Typography>
              <Divider sx={{ mb: 2 }} />
              <Typography color="text.secondary">
                Auction management coming soon...
              </Typography>
            </Paper>
          </Grid>
        </Grid>

        {/* Delete Confirmation Dialog */}
        <Dialog
          open={deleteDialogOpen}
          onClose={() => setDeleteDialogOpen(false)}
        >
          <DialogTitle>Confirm Delete</DialogTitle>
          <DialogContent>
            <Typography>
              Are you sure you want to delete user "{userToDelete?.username}"?
              This action cannot be undone.
            </Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
            <Button onClick={handleDeleteConfirm} color="error">
              Delete
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </Box>
  );
};

export default Admin;
