import { useState, useEffect } from "react";
import {
  Box,
  Container,
  Typography,
  Avatar,
  Grid,
  Paper,
  Button,
  TextField,
  Tab,
  Tabs,
  Card,
  CardContent,
  CardMedia,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@mui/material";
import { useParams } from "react-router-dom";
import { useAuth } from "../components/AuthContext";
import EditIcon from "@mui/icons-material/Edit";
import PhotoCamera from "@mui/icons-material/PhotoCamera";

interface Profile {
  username: string;
  email: string;
  bio: string;
  profile_picture: string | null;
  date_joined: string;
  active_auctions: Auction[];
  past_auctions: Auction[];
}

interface Auction {
  id: number;
  name: string;
  year: number;
  current_bid: number;
  time_left: string;
  images: { id: number; image: string; is_primary: boolean }[];
}

const Profile = () => {
  const { username } = useParams();
  const { isLoggedIn } = useAuth();
  const [profile, setProfile] = useState<Profile | null>(null);
  const [editMode, setEditMode] = useState(false);
  const [bio, setBio] = useState("");
  const [tabValue, setTabValue] = useState(0);
  const [profilePicture, setProfilePicture] = useState<File | null>(null);

  useEffect(() => {
    fetchProfile();
  }, [username]);

  const fetchProfile = async () => {
    try {
      const response = await fetch(`http://localhost:8000/api/auth/profile/`);
      if (response.ok) {
        const data = await response.json();
        setProfile(data);
        setBio(data.bio);
      }
    } catch (error) {
      console.error("Failed to fetch profile:", error);
    }
  };

  const handleSave = async () => {
    try {
      const formData = new FormData();
      formData.append("bio", bio);
      if (profilePicture) {
        formData.append("profile_picture", profilePicture);
      }

      const token = localStorage.getItem("accessToken");
      const response = await fetch(
        `http://localhost:8000/api/auth/profile/${username}/`,
        {
          method: "PATCH",
          headers: {
            Authorization: `Bearer ${token}`,
          },
          body: formData,
        }
      );

      if (response.ok) {
        const data = await response.json();
        setProfile(data);
        setEditMode(false);
      }
    } catch (error) {
      console.error("Failed to update profile:", error);
    }
  };

  const handleProfilePictureChange = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    if (event.target.files && event.target.files[0]) {
      setProfilePicture(event.target.files[0]);
    }
  };

  const getImageUrl = (imagePath: string) => {
    if (!imagePath) return "https://source.unsplash.com/random/800x600/?car";
    if (imagePath.startsWith("http")) return imagePath;
    return `http://localhost:8000${imagePath}`;
  };

  if (!profile) return null;

  return (
    <Box sx={{ py: 4, minHeight: "100vh" }}>
      <Container maxWidth="lg">
        <Paper sx={{ p: 4, mb: 4 }}>
          <Grid container spacing={4} alignItems="center">
            <Grid item>
              <Box sx={{ position: "relative" }}>
                <Avatar
                  src={profile.profile_picture || "/default-avatar.png"}
                  sx={{ width: 150, height: 150 }}
                />
                {isLoggedIn && profile.username === username && (
                  <input
                    accept="image/*"
                    style={{ display: "none" }}
                    id="profile-picture-input"
                    type="file"
                    onChange={handleProfilePictureChange}
                  />
                )}
              </Box>
            </Grid>
            <Grid item xs>
              <Typography variant="h4" gutterBottom>
                {profile.username}
              </Typography>
              <Typography variant="body1" color="text.secondary" gutterBottom>
                Member since{" "}
                {new Date(profile.date_joined).toLocaleDateString()}
              </Typography>
              {editMode ? (
                <TextField
                  fullWidth
                  multiline
                  rows={3}
                  value={bio}
                  onChange={(e) => setBio(e.target.value)}
                  sx={{ mt: 2 }}
                />
              ) : (
                <Typography variant="body1">
                  {profile.bio || "No bio yet"}
                </Typography>
              )}
            </Grid>
            {isLoggedIn && profile.username === username && (
              <Grid item>
                {editMode ? (
                  <Button variant="contained" onClick={handleSave}>
                    Save
                  </Button>
                ) : (
                  <Button
                    variant="outlined"
                    startIcon={<EditIcon />}
                    onClick={() => setEditMode(true)}
                  >
                    Edit Profile
                  </Button>
                )}
              </Grid>
            )}
          </Grid>
        </Paper>

        <Tabs
          value={tabValue}
          onChange={(_, newValue) => setTabValue(newValue)}
        >
          <Tab label="Active Auctions" />
          <Tab label="Past Auctions" />
        </Tabs>

        <Box sx={{ mt: 4 }}>
          <Grid container spacing={4}>
            {(tabValue === 0
              ? profile.active_auctions
              : profile.past_auctions
            ).map((auction) => (
              <Grid item key={auction.id} xs={12} sm={6} md={4}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={
                      auction.images[0]
                        ? getImageUrl(auction.images[0].image)
                        : "https://source.unsplash.com/random/800x600/?car"
                    }
                    alt={auction.name}
                  />
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {auction.year} {auction.name}
                    </Typography>
                    <Typography variant="body1" color="primary">
                      Current Bid: Â£{auction.current_bid.toLocaleString()}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {auction.time_left}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </Box>
  );
};

export default Profile;
